import { IoIosSearch } from 'react-icons/io'
import { useState, useEffect } from 'react'
export default function SearchBox({setDebouncedSearch}) {
    const [search, setSearch] = useState('')
    function debounce(func, wait, immediate) {
    	var timeout
    	return function() {
    		var context = this, args = arguments
    		var later = function() {
    			timeout = null
    			if (!immediate) func.apply(context, args)
    		}
    		var callNow = immediate && !timeout
    		clearTimeout(timeout)
    		timeout = setTimeout(later, wait)
    		if (callNow) func.apply(context, args)
    	    }
    }
    const searchHelper = (e) => debounce(() => setDebouncedSearch(e.target.value), 3000)
    return(
        <div className='flex no-wrap h-8 w-2/6 border border-black m-5'>
            <IoIosSearch className='w-8 my-auto'/>
            <input className='w-full outline-none' type='text' name='search' placeholder='Search Everywhere' onChange={ (e) => searchHelper(e) }/>
        </div>
    )
}
